import * as React from "react";
import { View, Text } from "react-native";
// NavigationContainer，是所有的路由都需要被包裹在这个里面
import { NavigationContainer } from "@react-navigation/native";
// 栈式导航
import { createStackNavigator } from "@react-navigation/stack";
import HomeScreen from "./components/HomeScreen";
import DetailsScreen from "./components/DetailsScreen";

const Stack = createStackNavigator();

// 有一个路由包裹器
// 中间包了一层栈式导航
// 栈式导航下面有一个页面，设置名称以及显示组件
function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator
        screenOptions={{
          title: "首页",
          headerStyle: {
            backgroundColor: "#97CA00",
          },
          headerTintColor: "#fff",
          headerTitleStyle: {
            fontWeight: "bold",
          },
        }}
      >
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{
            title: "首页",
            headerStyle: {
              backgroundColor: "#45111e",
            },
            headerTintColor: "#fff",
            headerTitleStyle: {
              fontWeight: "bold",
            },
          }}
        />
        <Stack.Screen
          name="DetailsScreen"
          component={DetailsScreen}
          options={{ title: "详情页标题" }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;
