import * as React from "react";
import { View, Text, Button } from "react-native";
// NavigationContainer，是所有的路由都需要被包裹在这个里面
import { NavigationContainer } from "@react-navigation/native";
// 栈式导航
import { createStackNavigator } from "@react-navigation/stack";
import HomeScreen from "./components/HomeScreen";
import DetailsScreen from "./components/DetailsScreen";
import Ionicons from "react-native-vector-icons/Ionicons";

const CustomTitle = (props) => {
  console.log("CustomTitle Props:", props);
  return (
    <View>
      <Text>自定义标题组件</Text>
    </View>
  );
};

const Stack = createStackNavigator();

// 有一个路由包裹器
// 中间包了一层栈式导航
// 栈式导航下面有一个页面，设置名称以及显示组件
function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator
        screenOptions={{
          title: "首页",
          headerStyle: {
            backgroundColor: "#97CA00",
          },
          headerTintColor: "#fff",
          headerTitleStyle: {
            fontWeight: "bold",
          },
        }}
      >
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{
            title: "首页",
            headerStyle: {
              backgroundColor: "#45111e",
            },
            headerTintColor: "#fff",
            headerTitleStyle: {
              fontWeight: "bold",
            },
            // 左边部分也设置自定义组件
            headerLeft: () => {
              return (
                <Ionicons
                  name="md-checkmark-circle"
                  size={32}
                  color="white"
                  style={{ marginLeft: 10 }}
                />
              );
            },
            headerRight: () => {
              return <Button title="右侧按钮" color="#fff" />;
            },
          }}
        />
        <Stack.Screen
          name="DetailsScreen"
          component={DetailsScreen}
          options={{
            headerTitle: (props) => {
              return <CustomTitle {...props} />;
            },
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;
