// 函数式组件，是没有state的
// useState
const App = () => {
  const [count, setCount] = useState(0);

  return (
    <View style={styles.container}>
      <Text>Count:{count}</Text>
      <Button
        title="count++"
        onPress={() => {
          setCount(count + 1);
        }}
      ></Button>
    </View>
  );
};

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
